mapper.select:
  select * 
	from (
    select A.*, rownum as rn
    from (
        select a.num, a.userid, a.age, a.gender, a.idx, a.review_star, a.title, a.content, a.review_count,
		a.image_file, a.reg_date, b.iname, b.picture, b.volume
		from p_review a
		full outer join p_items b
		on (a.idx = b.idx)
		where a.userid =#{userid}) A
	) where rn between #{start} and #{end}
