mapper.update:
update p_cart 
set cartid = (select nvl( max(cartid)+1, 1) from p_cart), num=#{num}
where idx=#{idx}

update p_cart
set num = num+1
where idx = #{idx}

update p_cart
set num = num-1
where idx = #{idx}

update p_order
 set delivery='주문 취소중'
 where orderid=#{orderid} and userid = #{userid}

update p_order
 set delivery='반품 요청중'
 where orderid=#{orderid} and userid = #{userid}
