mapper.select:
  select * 
	from (
    select A.*, rownum as rn
    from (
        select a.num, a.username, a.age, a.gender, a.idx, a.review_star, a.title, a.content, a.review_count,
		a.image_file, a.reg_date, b.iname, b.picture, b.volume
		from p_review a
		full outer join p_items b
		on (a.idx = b.idx)
		where a.username =#{userid}
		order by a.num desc) A
	) where rn between #{start} and #{end}

mapper.update:
  update p_review
	set  age=#{age}, review_star=#{review_star}
	   , title=#{title}, content=#{content}
	   , gender=#{gender}, image_file=#{image_file}
	where num=#{num}

mapper.select:
  select image_file
	from p_review
	where num=#{num}

  select * 
	from (
    select A.*, rownum as rn
    from (
        select a.num, a.username, a.age, a.gender, a.idx, a.review_star, a.title, a.content, a.review_count,
		a.image_file, a.reg_date, b.iname, b.picture, b.volume
		from p_review a
		full outer join p_items b
		on (a.idx = b.idx)
		where a.num is not null
        order by num desc) A
	) where rn between #{start} and #{end}

  select * 
	from (
    select A.*, rownum as rn
    from (
        select a.num, a.username, a.age, a.gender, a.idx, a.review_star, a.title, a.content, a.review_count,
		a.image_file, a.reg_date, b.iname, b.picture, b.volume
		from p_review a
		full outer join p_items b
		on (a.idx = b.idx)
		where a.image_file != ' '
        order by num desc) A
	) where rn between #{start} and #{end}

select username from p_review
where image_file != ' '
and num = #{num}
